import { createApp } from 'vue'; // Importa a função para criar a aplicação Vue
import App from './App.vue'; // Importa o componente principal da aplicação
import router from './router'; // Importa o Vue Router para navegação entre páginas
import 'bootstrap/dist/css/bootstrap.min.css'; // Importa o CSS do Bootstrap
import 'bootstrap/dist/js/bootstrap.bundle.min.js'; // Importa o JavaScript do Bootstrap

// Cria a instância da aplicação Vue
const app = createApp(App);

// Usa o Vue Router para habilitar navegação entre as páginas
app.use(router);

// Monta a aplicação no elemento HTML com o id "app"
app.mount('#app');
